const Header = (props) => {
  return (
    <h1>{props.course}</h1>
  )
}

const Part = (props) => {
  return(
    <p>{props.content} {props.exercise}</p>
  )
}

const Content = (props) => {
  const content = props.content.map((x,index)=><Part key={index} content={x.name} exercise={x.exercise} />)
  console.log(content)
  /** 
