const Header = (props) => {
  return (
    <h1>{props.course}</h1>
  )
}

const Content = (props) => {
  return(
    <>
      <p>
        Part1
      </p>
      <p>
        Part2
      </p>
      <p>
        Part3
      </p>
    </>
  )
}

const Total = (props) => {
  let {parts} = props
  let total = 0
  for (const item in parts){
   total =+ (parts[item].exercise)
  }
  return(
    <p>Number of exercises {total} </p>
  )
}

const App = () => {
  const course = {
    name: 'Half Stack application development', 
    parts: [
      {
        name: 'Fundamentals of React',
        exercise: 10
      },
      {
        name: 'Using props to pass data',
        exercise: 7
      },
      {
        name: 'State of a component',
        exercise: 14
      }
    ]
  }

  return (
    <div>
      <Header course={course.name}/>
      <Content />
      <Total parts={course.parts}/>
    </div>
  )
}

export default App
