const Header = ({course}) => {
  return (
    <h1>{course}</h1>
  )
}

const Part = ({content, exercises}) => {
  return(
    <p>{content} {exercises}</p>
  )
}

const Content = ({content}) => {
  const newContent = content.map((x)=><Part key={x.id} content={x.name} exercises={x.exercises} />)
  //console.log('content: ', content)

 return(
  <div>
    {newContent}
  </div>
 )
}

const Course = ({course}) => {
  console.log('course: ', course)
  const courseName = course.map((x)=><p>{x.name}</p>)
  return(
    <>Working</>
  )
}

const Total = ({total}) => {
  const totalExercises = total.reduce(
      (sum, exercise) => sum + exercise.exercises,0
    )
  return (
    <h4>Total of {totalExercises} exercises</h4>
  )
}

const App = () => {
  const course = [
    {
    id: 1,
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      },
      {
        name: 'Redux',
        exercises: 11,
        id: 4
      }
    ]
  }
]

  return <Course course={course} />
}

export default App
